import './RenderExpenses.css';
import ExpenseItem from './ExpenseItem'
import Card from './Card'
import FilterYear from './FilterYear';
import React, { useState } from 'react';


function RenderExpenses(props) {
    const [filteredYear, setFilteredYear] = useState('2022');

    const filterChangeHandler = selectedYear => {
        setFilteredYear(selectedYear);
    };

    return (<div>

        <Card className="expenses"><FilterYear selected={filteredYear} onFilterChange={filterChangeHandler}></FilterYear><ExpenseItem title={props.expenses[0].title} amount={props.expenses[0].amount} date={props.expenses[0].date}></ExpenseItem>
            <ExpenseItem title={props.expenses[1].title} amount={props.expenses[1].amount} date={props.expenses[1].date}></ExpenseItem>
            <ExpenseItem title={props.expenses[2].title} amount={props.expenses[2].amount} date={props.expenses[2].date}></ExpenseItem></Card></div>)
}

export default RenderExpenses;
